        allowNull: false,
        type: DataTypes.ENUM(jobStatus),
        defaultValue: jobStatusDefault,
      },
      experience: {
        allowNull: false,
        type: DataTypes.ENUM(jobExperienced),
        defaultValue: jobExperiencedDefault,
      },
      isRemote: {
        allowNull: false,
        type: DataTypes.BOOLEAN,
        defaultValue: false,
      },
      Category_id: {
        allowNull: false,
        type: DataTypes.STRING(36),
        defaultValue: DataTypes.UUIDV4,
        references: {
          model: "Categories",
          key: "id",
        },
      },
      Campaign_id: {
        allowNull: false,
        type: DataTypes.STRING(36),
        defaultValue: DataTypes.UUIDV4,
        references: {
          model: "Campaigns",
          key: "id",
        },
      },
    },
    {
      sequelize,
      modelName: "Jobs",
      timestamps: false,
      createdAt: false,
      updatedAt: false,
    }
  );
  return Jobs;
};

