  return new Promise(async (resolve, reject) => {
    try {
      let job = await jobRepository.getJobsById(jobID);
      if (!job) {
        resolve({
          errMsg: "Job not found",
        });
      }
      await jobRepository.updateStatus({ id: jobID });

      resolve({
        errCode: 0,
