  findOne,
  createNewMember,
  getMemberByEmail,
  getMemberById,
  update,
  showAllMember,
  updateStatus,
  getCodeByEmail,
  sendMail,
  // updatePass
  updatePass,
  getMemberByEmailActive,
};

