      // define association here
      Categories.hasMany(models.Jobs, {
        foreignKey: "Category_id"
      });
    }

  }

  Categories.init({
    id: {
      allowNull: false,
      primaryKey: true,
      type: DataTypes.STRING(36),
      defaultValue: DataTypes.UUIDV4
    },
    name: {
      allowNull: false,
