  try {
    const id = req.params.id;
    const staff = await getStaffById(id);
    if (!staff) {
      throw new ApiError(httpStatus.NOT_FOUND, "Staff not found");
    }
    await updateStatus({ id: id });

  } catch (error) {
    throw error;
  }
}
module.exports = {
  updateStaffProfile,
  updateStaffStatusToHidden
};

