    return result;
  } catch (error) {
    throw error;
  }
};

const getReportByMember = async (memberID) => {
  try {
    const report = await reportRepository.getReportByMemberID(memberID);
    return report;
  } catch (error) {
    throw error;
  }
};

module.exports = {
  getAllReports,
  getAllReportsByInterviewer,
  updateInterviewers,
  updateMark,
  getReportByMember,
};

