    return (
        await Categories.update({ status: "Hidden" }, {
            where: where,
        }
        )
    )
};

const createCategory = async (data) => {
    return (
        await Categories.create({
            name: data.name,
            description: data.description,
        })
    )
}
module.exports = { update, updateStatus, findAllCate, findOne, createCategory };

